import "core-js/modules/es6.regexp.split";
import "core-js/modules/es6.regexp.match";
import "core-js/modules/es6.regexp.search";

/**
 * EXAMPLE ?id=123&name=abc
 * RETURN Object {id:123,name:abc}
 */
export function urlParse() {
  var parm = window.location.search;
  parm = decodeURIComponent(parm);
  var object = {};
  /*这是一种方法
  parm = parm.substr(1)
  let reg = /([^&]+)=([^&]+)&?/
  while (reg.test(parm)) {
    object[RegExp.$1] = RegExp.$2
    parm = parm.replace(reg, '')
    console.log(parm)
  }
  */

  /* 这是另一种方法 */

  var reg = /[?&][^?&]+=[^&]+/g;
  var arr = parm.match(reg); // ['?id=123', '&name=abc']

  if (arr) {
    arr.forEach(function (item) {
      var tempArr = item.substr(1).split('=');
      var key = tempArr[0];
      var value = tempArr[1];
      object[key] = value;
    });
  }

  return object;
}
