import Scroll from 'better-scroll';
import star from '../star/star.vue';
import ratinglist from '../ratinglist/ratinglist.vue';
import inputtime from '../inputtime/inputtime.vue';
import Bus from '../Bus/Bus.js';
import iconthumb from '../iconthumb/iconthumb.vue';
export default {
  props: {
    seller: {
      type: Object,
      required: true
    },
    ratings: {
      type: Object,
      required: true
    }
  },
  data: function data() {
    return {
      ratingsList: []
    };
  },
  watch: {
    ratings: function ratings() {
      var _this = this;

      this.ratingsList = this.ratings.ratings;
      this.$nextTick(function () {
        _this.scroll.refresh();
      });
    }
  },
  mounted: function mounted() {
    var _this2 = this;

    //切换路由后，data中的数据会初始化
    if (this.ratings.ratings) this.ratingsList = this.ratings.ratings;

    if (!this.scroll) {
      this.scroll = new Scroll(this.$refs.ratings, {
        click: true
      });
    }

    Bus.$on('changeratingtype', function (ratingList) {
      _this2.ratingsList = ratingList;
    });
  },
  components: {
    star: star,
    ratinglist: ratinglist,
    inputtime: inputtime,
    // eslint-disable-next-line
    Bus: Bus,
    iconthumb: iconthumb
  }
};
