const starLength = 5
const on = 'on'
const off = 'off'
const half = 'half'

export default {
    props: {
        score: {
            type: Number,
            requierd: true
        },
        size: {
            type: Number,
            required: true
        },
        margin: {
            type: Number
        }
    },
    computed: {
        stars: function () {
            let stars = []
            const score = Math.floor(this.score * 2) / 2
            const halfNumber = score % 1 !== 0
            const onNumber = Math.floor(score)
            for (let i = 0; i < onNumber; i++) {
                stars.push(on)
            }
            if (halfNumber) {
                stars.push(half)
            }
            while (stars.length < starLength) {
                stars.push(off)
            }
            return stars
        }
    }
}
