export default {
  props: {
    food: {
      type: Object,
      required: true
    },
    selectfoods: {
      type: Array,
      default() {
        return []
      }
    }
  },

  data() {
    return {};
  },

  methods: {
    addfood: function (food, event) {
      if (!event._constructed) {
        return
      }
      else {
        if (!food.count) {
          this.$set(food, 'count', 1)
          this.selectfoods.push(food)
        }
        else {
          food.count++
        }
      }
    },
    removefood: function (food, event) {
      if (!event._constructed) {
        return
      }
      else {
        if (food.count) {
          food.count--
          if (!food.count) {
            this.selectfoods.forEach((item, index, array) => {
              if (item === food) {
                array.splice(index, 1)
                return
              }
            })
          }
        }
      }
    }
  }
};
