import {urlParse} from './common/js/urlParse.js'
import vheader from "./components/header/header.vue"
import shopcart from './components/shopcart/shopcart.vue'

const ERROK = 0

export default {
  data () {
    return {
      seller: {
        id: (() => {
          let queryParm = urlParse()
          return queryParm.id
        })()
      },
      ratings: {},
      selectfoods: [],
      shopcartComponent: {}
    }
  },
  created: function () {
    this.$http.get('/api/seller?id=' + this.seller.id)
    .then((res) => {
      if (res.data.errno === ERROK) {
        this.seller = Object.assign({}, this.seller, res.data.data)
      }
      else new Error()
    })
    .catch((error) => {
      console.log(error)
    })

    this.$http.get('/api/ratings')
    .then((res) => {
      if (res.data.errno === ERROK) {
        this.$set(this.ratings, 'ratings', res.data.data)
      }
      else {
        throw new Error()
      }
    })
    .catch((error) => {
      console.log(error)
    })
  },
  mounted: function () {
    this.shopcartComponent = this.$refs.shopcart
  },
  components: {
    vheader,
    shopcart
  }
}
