import star from '../star/star.vue'
import vtitle from '../title/title.vue'

export default {
  data() {
    return {
      //不同的type映射到不同的class
      classMap: ['decrease', 'discount', 'special', 'invoice', 'guarantee'],
      show: false
    }
  },
  props: {
    seller: {
      type: Object
    }
  },
  methods: {
    showDetail: function () {
      this.show = !this.show
    },
    enter: function (el) {
      //过渡关闭弹窗后, 下次打开, 滚动条到顶部而不是上次关闭时的位置
      el.scrollTop = 0
    }
  },
  components: {
    star,
    vtitle
  }
}
